      GET: (id: string) => `/api/problems/${id}`,
      CREATE: '/api/problems',
      UPDATE: (id: string) => `/api/problems/${id}`,
      DELETE: (id: string) => `/api/problems/${id}`,
      RANDOM: '/api/problems/random',
