    public DateTime SolvedAt { get; set; } = DateTime.UtcNow;
}

public class CodeExecutionRequestedEvent
{
    public string RequestId { get; set; } = string.Empty;
    public string UserId { get; set; } = string.Empty;
    public string ProblemId { get; set; } = string.Empty;
    public string Code { get; set; } = string.Empty;
    public string Language { get; set; } = string.Empty;
    public bool IsSubmission { get; set; } // true for submission, false for test run
    public DateTime RequestedAt { get; set; } = DateTime.UtcNow;
}

public class CodeExecutionCompletedEvent
{
    public string RequestId { get; set; } = string.Empty;
    public string UserId { get; set; } = string.Empty;
    public string Status { get; set; } = string.Empty;
    public Dictionary<string, object> Results { get; set; } = new();
    public DateTime CompletedAt { get; set; } = DateTime.UtcNow;
}

client/src/components/admin/BulkImport.t.content:
import { useState, useRef } from 'react';
import { useMutation } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface ImportResult {
  total: number;
  imported: number;
  errors: Array<{
    row: number;
    error: string;
    data: any;
  }>;
}

export function BulkImport() {
  const { toast } = useToast();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [importResult, setImportResult] = useState<ImportResult | null>(null);
  const [isUploading, setIsUploading] = useState(false);

  const uploadMutation = useMutation({
    mutationFn: async (file: File) => {
      setIsUploading(true);
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('/api/admin/bulk-import', {
        method: 'POST',
        body: formData,
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`,
        },
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Upload failed');
      }

      return response.json();
    },
    onSuccess: (result: ImportResult) => {
      setImportResult(result);
      toast({
        title: 'Import Complete',
        description: `Successfully imported ${result.imported} out of ${result.total} problems.`,
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Import Failed',
        description: error.message || 'Failed to import problems',
        variant: 'destructive',
      });
    },
    onSettled: () => {
      setIsUploading(false);
    },
  });

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      // Validate file type
      const allowedTypes = [
        'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', // .xlsx
        'application/vnd.ms-excel', // .xls
        'text/csv', // .csv
      ];

      if (!allowedTypes.includes(file.type)) {
        toast({
          title: 'Invalid File Type',
          description: 'Please select an Excel (.xlsx, .xls) or CSV file.',
          variant: 'destructive',
        });
        return;
      }

      setSelectedFile(file);
      setImportResult(null);
    }
  };

  const handleUpload = () => {
    if (!selectedFile) {
      toast({
        title: 'No File Selected',
        description: 'Please select a file to upload.',
        variant: 'destructive',
      });
      return;
    }

    uploadMutation.mutate(selectedFile);
  };

  const resetUpload = () => {
    setSelectedFile(null);
    setImportResult(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const downloadTemplate = () => {
    // Create a sample CSV template
    const headers = [
      'title',
      'description',
      'difficulty',
      'categorySlug',
      'tags',
      'isPremium',
      'constraints',
      'examples',
      'testCases',
      'solution',
      'hints'
    ];

    const sampleRow = [
      'Two Sum',
      'Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.',
      'Easy',
      'arrays-hashing',
      'Array,Hash Table',
      'false',
      '2 â‰¤ nums.length â‰¤ 10â´',
      '[{"input":"nums = [2,7,11,15], target = 9","output":"[0,1]","explanation":"Because nums[0] + nums[1] == 9, we return [0, 1]."}]',
      '[{"input":{"nums":[2,7,11,15],"target":9},"output":[0,1]}]',
      'def twoSum(self, nums, target):\n    hashmap = {}\n    for i, num in enumerate(nums):\n        complement = target - num\n        if complement in hashmap:\n            return [hashmap[complement], i]\n        hashmap[num] = i',
      '["Think about using a hash map","Store numbers you\'ve seen with their indices"]'
    ];

    const csvContent = [headers, sampleRow]
      .map(row => row.map(field => `"${field}"`).join(','))
      .join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'problems-template.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-xl font-semibold text-slate-900 dark:text-white">
            Bulk Import Problems
          </h3>
          <p className="text-slate-600 dark:text-slate-400 mt-1">
            Upload Excel or CSV files to import multiple problems at once
          </p>
        </div>

        <Button
          variant="outline"
          onClick={downloadTemplate}
          data-testid="button-download-template"
        >
          <i className="fas fa-download mr-2"></i>
          Download Template
        </Button>
      </div>

      <Tabs defaultValue="upload" className="space-y-6">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="upload">Upload File</TabsTrigger>
          <TabsTrigger value="format">Format Guide</TabsTrigger>
        </TabsList>

        <TabsContent value="upload" className="space-y-6">
          {/* Upload Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <i className="fas fa-upload mr-2"></i>
                File Upload
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* File Selection */}
              <div>
                <Label htmlFor="file">Select File</Label>
                <div className="mt-2">
                  <Input
                    ref={fileInputRef}
                    id="file"
                    type="file"
                    accept=".xlsx,.xls,.csv"
                    onChange={handleFileSelect}
                    disabled={isUploading}
                    data-testid="input-bulk-import-file"
                  />
                  <p className="text-sm text-slate-500 mt-2">
                    Supported formats: Excel (.xlsx, .xls) and CSV files
                  </p>
                </div>
              </div>

              {/* Selected File Info */}
              {selectedFile && (
                <Alert>
                  <i className="fas fa-file"></i>
                  <AlertDescription>
                    <div className="flex items-center justify-between">
                      <div>
                        <strong>{selectedFile.name}</strong>
                        <span className="ml-2 text-slate-500">
                          ({(selectedFile.size / 1024).toFixed(1)} KB)
                        </span>
                      </div>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={resetUpload}
                        disabled={isUploading}
                        data-testid="button-reset-upload"
                      >
                        <i className="fas fa-times"></i>
                      </Button>
                    </div>
                  </AlertDescription>
                </Alert>
              )}

              {/* Upload Progress */}
              {isUploading && (
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <span className="text-sm font-medium">Importing problems...</span>
                    <span className="text-sm text-slate-500">Processing with AI</span>
                  </div>
                  <Progress value={75} className="w-full" />
                  <p className="text-xs text-slate-500">
                    Using AI to fill in missing data and generate test cases...
                  </p>
                </div>
              )}

              {/* Upload Button */}
              <Button
                onClick={handleUpload}
                disabled={!selectedFile || isUploading}
                className="w-full bg-brand-600 hover:bg-brand-700"
                data-testid="button-start-import"
              >
                {isUploading ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Importing...
                  </>
                ) : (
                  <>
                    <i className="fas fa-upload mr-2"></i>
                    Start Import
                  </>
                )}
              </Button>
            </CardContent>
          </Card>

          {/* Import Results */}
          {importResult && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <i className="fas fa-chart-bar mr-2"></i>
                  Import Results
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                  <div className="text-center p-4 bg-slate-50 dark:bg-slate-800 rounded-lg">
                    <div className="text-2xl font-bold text-slate-900 dark:text-white">
                      {importResult.total}
                    </div>
                    <div className="text-sm text-slate-600 dark:text-slate-400">Total Rows</div>
                  </div>

                  <div className="text-center p-4 bg-success-50 dark:bg-success-900/20 rounded-lg">
                    <div className="text-2xl font-bold text-success-700 dark:text-success-400">
                      {importResult.imported}
                    </div>
                    <div className="text-sm text-success-600 dark:text-success-400">Successfully Imported</div>
                  </div>

                  <div className="text-center p-4 bg-error-50 dark:bg-error-900/20 rounded-lg">
                    <div className="text-2xl font-bold text-error-700 dark:text-error-400">
                      {importResult.errors.length}
                    </div>
                    <div className="text-sm text-error-600 dark:text-error-400">Errors</div>
                  </div>
                </div>

                {/* Error Details */}
                {importResult.errors.length > 0 && (
                  <div>
                    <h4 className="font-semibold text-slate-900 dark:text-white mb-3">
                      Import Errors
                    </h4>
                    <ScrollArea className="h-64 border border-slate-200 dark:border-slate-700 rounded-lg">
                      <div className="p-4 space-y-3">
                        {importResult.errors.map((error, index) => (
                          <div key={index} className="border-l-4 border-error-500 pl-4 py-2">
                            <div className="flex items-center justify-between mb-1">
                              <Badge variant="destructive">Row {error.row}</Badge>
                            </div>
                            <p className="text-sm text-error-700 dark:text-error-400 mb-2">
                              {error.error}
                            </p>
                            <details className="text-xs">
                              <summary className="cursor-pointer text-slate-500 hover:text-slate-700">
                                View row data
                              </summary>
                              <pre className="mt-2 p-2 bg-slate-100 dark:bg-slate-800 rounded text-xs overflow-x-auto">
                                {JSON.stringify(error.data, null, 2)}
                              </pre>
                            </details>
                          </div>
                        ))}
                      </div>
                    </ScrollArea>
                  </div>
                )}
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="format" className="space-y-6">
          {/* Format Guide */}
          <Card>
            <CardHeader>
              <CardTitle>File Format Requirements</CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <Alert>
                <i className="fas fa-info-circle"></i>
                <AlertDescription>
                  Your file must include these required columns. Missing data will be filled automatically using AI.
                </AlertDescription>
              </Alert>

              <div className="space-y-4">
                <h4 className="font-semibold text-slate-900 dark:text-white">Required Columns:</h4>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-3">
                    <div className="border border-slate-200 dark:border-slate-700 rounded-lg p-3">
                      <h5 className="font-medium text-slate-900 dark:text-white">title</h5>
                      <p className="text-sm text-slate-600 dark:text-slate-400">Problem title (required)</p>
                      <code className="text-xs bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded">Two Sum</code>
                    </div>

                    <div className="border border-slate-200 dark:border-slate-700 rounded-lg p-3">
                      <h5 className="font-medium text-slate-900 dark:text-white">description</h5>
                      <p className="text-sm text-slate-600 dark:text-slate-400">Problem description (AI fillable)</p>
                      <code className="text-xs bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded">Given an array...</code>
                    </div>

                    <div className="border border-slate-200 dark:border-slate-700 rounded-lg p-3">
                      <h5 className="font-medium text-slate-900 dark:text-white">difficulty</h5>
                      <p className="text-sm text-slate-600 dark:text-slate-400">Easy, Medium, or Hard (AI fillable)</p>
                      <code className="text-xs bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded">Easy</code>
                    </div>

                    <div className="border border-slate-200 dark:border-slate-700 rounded-lg p-3">
                      <h5 className="font-medium text-slate-900 dark:text-white">categorySlug</h5>
                      <p className="text-sm text-slate-600 dark:text-slate-400">Category identifier</p>
                      <code className="text-xs bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded">arrays-hashing</code>
                    </div>
                  </div>

                  <div className="space-y-3">
                    <div className="border border-slate-200 dark:border-slate-700 rounded-lg p-3">
                      <h5 className="font-medium text-slate-900 dark:text-white">tags</h5>
                      <p className="text-sm text-slate-600 dark:text-slate-400">Comma-separated tags</p>
                      <code className="text-xs bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded">Array,Hash Table</code>
                    </div>

                    <div className="border border-slate-200 dark:border-slate-700 rounded-lg p-3">
                      <h5 className="font-medium text-slate-900 dark:text-white">isPremium</h5>
                      <p className="text-sm text-slate-600 dark:text-slate-400">true or false</p>
                      <code className="text-xs bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded">false</code>
                    </div>

                    <div className="border border-slate-200 dark:border-slate-700 rounded-lg p-3">
                      <h5 className="font-medium text-slate-900 dark:text-white">testCases</h5>
                      <p className="text-sm text-slate-600 dark:text-slate-400">JSON array (AI generated if empty)</p>
                      <code className="text-xs bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded">[{`"input": "...", "output": "..."`}]</code>
                    </div>

                    <div className="border border-slate-200 dark:border-slate-700 rounded-lg p-3">
                      <h5 className="font-medium text-slate-900 dark:text-white">solution</h5>
                      <p className="text-sm text-slate-600 dark:text-slate-400">Optional solution code</p>
                      <code className="text-xs bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded">def twoSum...</code>
                    </div>
                  </div>
                </div>
              </div>

              <Alert className="bg-blue-50 dark:bg-blue-900/20 border-blue-200 dark:border-blue-800">
                <i className="fas fa-robot text-blue-600"></i>
                <AlertDescription>
                  <strong>AI-Powered Import:</strong> Missing descriptions, difficulties, tags, and test cases 
                  will be automatically generated using Perplexity AI. This ensures high-quality, complete problem data.
                </AlertDescription>
              </Alert>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/components/admin/CategoryConf.content:
import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent } from '@/components/ui/card';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Badge } from '@/components/ui/badge';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Category } from '@/types/api';

export function CategoryConfig() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingCategory, setEditingCategory] = useState<Category | null>(null);
  const [isAddingCategory, setIsAddingCategory] = useState(false);
  const [newCategory, setNewCategory] = useState({
    name: '',
    slug: '',
    description: '',
    freeProblemLimit: 10,
  });

  const { data: categories = [], isLoading } = useQuery<Category[]>({
    queryKey: ['/api/categories'],
  });

  const updateCategoryMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: Partial<Category> }) => {
      const response = await apiRequest('PUT', `/api/categories/${id}`, updates);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: 'Success',
        description: 'Category updated successfully!',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
      setEditingCategory(null);
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to update category',
        variant: 'destructive',
      });
    },
  });

  const createCategoryMutation = useMutation({
    mutationFn: async (categoryData: any) => {
      const response = await apiRequest('POST', '/api/categories', categoryData);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: 'Success',
        description: 'Category created successfully!',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
      setIsAddingCategory(false);
      setNewCategory({
        name: '',
        slug: '',
        description: '',
        freeProblemLimit: 10,
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to create category',
        variant: 'destructive',
      });
    },
  });

  const handleFreeLimitChange = (categoryId: string, newLimit: number) => {
    updateCategoryMutation.mutate({
      id: categoryId,
      updates: { freeProblemLimit: newLimit },
    });
  };

  const generateSlug = (name: string) => {
    return name
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, '-')
      .replace(/^-|-$/g, '');
  };

  const handleNameChange = (value: string) => {
    setNewCategory(prev => ({
      ...prev,
      name: value,
      slug: generateSlug(value),
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newCategory.name || !newCategory.slug) {
      toast({
        title: 'Error',
        description: 'Please fill in all required fields',
        variant: 'destructive',
      });
      return;
    }
    createCategoryMutation.mutate(newCategory);
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        {[...Array(6)].map((_, i) => (
          <Card key={i} className="animate-pulse">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <div className="h-5 bg-slate-200 dark:bg-slate-700 rounded w-1/3 mb-2"></div>
                  <div className="h-4 bg-slate-200 dark:bg-slate-700 rounded w-1/2"></div>
                </div>
                <div className="flex items-center space-x-4">
                  <div className="w-20 h-8 bg-slate-200 dark:bg-slate-700 rounded"></div>
                  <div className="w-8 h-8 bg-slate-200 dark:bg-slate-700 rounded"></div>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-xl font-semibold text-slate-900 dark:text-white">
            Category Configuration
          </h3>
          <p className="text-slate-600 dark:text-slate-400 mt-1">
            Configure how many problems in each category are free vs premium
          </p>
        </div>

        <Dialog open={isAddingCategory} onOpenChange={setIsAddingCategory}>
          <DialogTrigger asChild>
            <Button className="bg-brand-600 hover:bg-brand-700" data-testid="button-add-category">
              <i className="fas fa-plus mr-2"></i>
              Add Category
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Add New Category</DialogTitle>
            </DialogHeader>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="name" className="required">Name</Label>
                <Input
                  id="name"
                  value={newCategory.name}
                  onChange={(e) => handleNameChange(e.target.value)}
                  placeholder="Arrays & Hashing"
                  required
                  data-testid="input-category-name"
                />
              </div>

              <div>
                <Label htmlFor="slug">Slug</Label>
                <Input
                  id="slug"
                  value={newCategory.slug}
                  onChange={(e) => setNewCategory(prev => ({ ...prev, slug: e.target.value }))}
                  placeholder="arrays-hashing"
                  data-testid="input-category-slug"
                />
                <p className="text-xs text-slate-500 mt-1">
                  Auto-generated from name. Used in URLs.
                </p>
              </div>

              <div>
                <Label htmlFor="description">Description</Label>
                <Input
                  id="description"
                  value={newCategory.description}
                  onChange={(e) => setNewCategory(prev => ({ ...prev, description: e.target.value }))}
                  placeholder="Array manipulation and hash table problems"
                  data-testid="input-category-description"
                />
              </div>

              <div>
                <Label htmlFor="freeProblemLimit">Free Problems Limit</Label>
                <Input
                  id="freeProblemLimit"
                  type="number"
                  min="0"
                  value={newCategory.freeProblemLimit}
                  onChange={(e) => setNewCategory(prev => ({ ...prev, freeProblemLimit: parseInt(e.target.value) || 0 }))}
                  data-testid="input-category-free-limit"
                />
                <p className="text-xs text-slate-500 mt-1">
                  Number of problems that can be accessed for free in this category
                </p>
              </div>

              <div className="flex justify-end space-x-3 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsAddingCategory(false)}
                  data-testid="button-cancel-category"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  disabled={createCategoryMutation.isPending}
                  className="bg-brand-600 hover:bg-brand-700"
                  data-testid="button-save-category"
                >
                  {createCategoryMutation.isPending ? (
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                  ) : null}
                  Create Category
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Categories List */}
      <div className="space-y-4">
        {categories.map((category) => (
          <Card key={category.id}>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <div className="flex items-center space-x-3 mb-2">
                    <h4 className="text-lg font-medium text-slate-900 dark:text-white">
                      {category.name}
                    </h4>
                    <Badge variant="outline">
                      {category.totalProblems} total problems
                    </Badge>
                  </div>
                  <p className="text-sm text-slate-600 dark:text-slate-400 mb-3">
                    {category.description}
                  </p>

                  {/* Progress Bar */}
                  <div className="flex items-center space-x-3">
                    <div className="flex-1">
                      <div className="flex items-center justify-between text-xs text-slate-600 dark:text-slate-400 mb-1">
                        <span>Free: {category.freeProblemLimit}</span>
                        <span>Premium: {Math.max(0, category.totalProblems - category.freeProblemLimit)}</span>
                      </div>
                      <div className="w-full bg-slate-200 dark:bg-slate-700 rounded-full h-2">
                        <div
                          className="bg-success-600 h-2 rounded-full transition-all"
                          style={{
                            width: `${category.totalProblems > 0 ? (category.freeProblemLimit / category.totalProblems) * 100 : 0}%`
                          }}
                        ></div>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="flex items-center space-x-4 ml-6">
                  {/* Free Limit Input */}
                  <div className="flex items-center space-x-2">
                    <Label htmlFor={`limit-${category.id}`} className="text-sm whitespace-nowrap">
                      Free problems:
                    </Label>
                    <Input
                      id={`limit-${category.id}`}
                      type="number"
                      min="0"
                      max={category.totalProblems}
                      value={editingCategory?.id === category.id ? editingCategory.freeProblemLimit : category.freeProblemLimit}
                      onChange={(e) => {
                        const newLimit = parseInt(e.target.value) || 0;
                        if (editingCategory?.id === category.id) {
                          setEditingCategory({ ...editingCategory, freeProblemLimit: newLimit });
                        } else {
                          setEditingCategory({ ...category, freeProblemLimit: newLimit });
                        }
                      }}
                      onBlur={() => {
                        if (editingCategory?.id === category.id && editingCategory.freeProblemLimit !== category.freeProblemLimit) {
                          handleFreeLimitChange(category.id, editingCategory.freeProblemLimit);
                        }
                      }}
                      onKeyDown={(e) => {
                        if (e.key === 'Enter' && editingCategory?.id === category.id) {
                          handleFreeLimitChange(category.id, editingCategory.freeProblemLimit);
                        }
                      }}
                      className="w-20"
                      data-testid={`input-free-limit-${category.slug}`}
                    />
                  </div>

                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      if (editingCategory?.id === category.id) {
                        handleFreeLimitChange(category.id, editingCategory.freeProblemLimit);
                      }
                    }}
                    disabled={updateCategoryMutation.isPending}
                    data-testid={`button-save-${category.slug}`}
                  >
                    {updateCategoryMutation.isPending ? (
                      <i className="fas fa-spinner fa-spin"></i>
                    ) : (
                      <i className="fas fa-save"></i>
                    )}
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {categories.length === 0 && (
        <Card>
          <CardContent className="p-12 text-center">
            <i className="fas fa-folder text-4xl text-slate-300 dark:text-slate-600 mb-4"></i>
            <h4 className="text-lg font-medium text-slate-900 dark:text-white mb-2">
              No categories yet
            </h4>
            <p className="text-slate-600 dark:text-slate-400">
              Create your first category to organize problems.
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

