                  <X className="h-5 w-5" />
                ) : (
                  <Menu className="h-5 w-5" />
                )}
              </Button>
            </div>
          </div>

          {/* Mobile Navigation */}
          {mobileMenuOpen && (
            <div className="md:hidden border-t">
              <div className="space-y-1 px-2 pb-4 pt-2">
                {navItems.map((item) => (
                  <Link key={item.href} href={item.href}>
