import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Category } from '@/types/api';

interface ProblemFiltersProps {
  categories: Category[];
  filters: {
    categoryId?: string;
    difficulty?: string;
    isPaid?: boolean;
    search?: string;
  };
  onFiltersChange: (filters: any) => void;
}

export function ProblemFilters({ categories, filters, onFiltersChange }: ProblemFiltersProps) {
  const [searchValue, setSearchValue] = useState(filters.search || '');

  const handleFilterChange = (key: string, value: any) => {
    const newFilters = { ...filters };

    if (value === 'all' || value === '' || value === undefined) {
      delete newFilters[key as keyof typeof newFilters];
    } else {
      (newFilters as any)[key] = value;
    }

    onFiltersChange(newFilters);
  };

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearchValue(value);

    // Debounce search
    const timeoutId = setTimeout(() => {
      handleFilterChange('search', value || undefined);
    }, 300);

    return () => clearTimeout(timeoutId);
  };

  const clearFilters = () => {
    setSearchValue('');
    onFiltersChange({});
  };

  const activeFiltersCount = Object.keys(filters).length;

  return (
    <Card className="mb-6">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-slate-900 dark:text-white">
            Filter Problems
          </h3>
          {activeFiltersCount > 0 && (
            <div className="flex items-center space-x-2">
              <Badge variant="outline">{activeFiltersCount} filter(s) active</Badge>
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={clearFilters}
                data-testid="button-clear-filters"
              >
                Clear All
              </Button>
            </div>
          )}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          {/* Search */}
          <div>
            <Label htmlFor="search" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-2 block">
              Search
            </Label>
            <div className="relative">
              <Input
                id="search"
                type="text"
                placeholder="Search problems..."
                value={searchValue}
                onChange={handleSearchChange}
                className="pl-10"
                data-testid="input-search-problems"
              />
              <i className="fas fa-search absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400"></i>
            </div>
          </div>

          {/* Category */}
          <div>
            <Label htmlFor="category" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-2 block">
              Category
            </Label>
            <Select 
              value={filters.categoryId || 'all'} 
              onValueChange={(value) => handleFilterChange('categoryId', value)}
            >
              <SelectTrigger data-testid="select-category">
                <SelectValue placeholder="All Categories" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Categories</SelectItem>
                {categories.map((category) => (
                  <SelectItem key={category.id} value={category.id}>
                    {category.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Difficulty */}
          <div>
            <Label htmlFor="difficulty" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-2 block">
              Difficulty
            </Label>
            <Select 
              value={filters.difficulty || 'all'} 
              onValueChange={(value) => handleFilterChange('difficulty', value)}
            >
              <SelectTrigger data-testid="select-difficulty">
                <SelectValue placeholder="All Levels" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Levels</SelectItem>
                <SelectItem value="Easy">Easy</SelectItem>
                <SelectItem value="Medium">Medium</SelectItem>
                <SelectItem value="Hard">Hard</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Access Level */}
          <div>
            <Label htmlFor="access" className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-2 block">
              Access
            </Label>
            <Select 
              value={
                filters.isPaid === undefined ? 'all' : 
                filters.isPaid ? 'premium' : 'free'
              } 
              onValueChange={(value) => {
                if (value === 'all') {
                  handleFilterChange('isPaid', undefined);
                } else {
                  handleFilterChange('isPaid', value === 'premium');
                }
              }}
            >
              <SelectTrigger data-testid="select-access">
                <SelectValue placeholder="All Access" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Access</SelectItem>
                <SelectItem value="free">Free</SelectItem>
                <SelectItem value="premium">Premium</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        {/* Quick Filters */}
        <div className="mt-4 pt-4 border-t border-slate-200 dark:border-slate-700">
          <Label className="text-sm font-medium text-slate-700 dark:text-slate-300 mb-3 block">
            Quick Filters
          </Label>
          <div className="flex flex-wrap gap-2">
            <Button
              variant={filters.difficulty === 'Easy' ? 'default' : 'outline'}
              size="sm"
              onClick={() => handleFilterChange('difficulty', filters.difficulty === 'Easy' ? undefined : 'Easy')}
              className="h-8"
              data-testid="button-filter-easy"
            >
              Easy Problems
            </Button>
            <Button
              variant={filters.isPaid === false ? 'default' : 'outline'}
              size="sm"
              onClick={() => handleFilterChange('isPaid', filters.isPaid === false ? undefined : false)}
              className="h-8"
              data-testid="button-filter-free"
            >
              Free Only
            </Button>
            <Button
              variant={filters.isPaid === true ? 'default' : 'outline'}
              size="sm"
              onClick={() => handleFilterChange('isPaid', filters.isPaid === true ? undefined : true)}
              className="h-8"
              data-testid="button-filter-premium"
            >
              Premium Only
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/problems/ProblemTa.content:
import { useState } from 'react';
import { Link } from 'wouter';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { Problem } from '@/types/api';
import { useAuth } from '@/hooks/useAuth';

interface ProblemTableProps {
  problems: Problem[];
  isLoading?: boolean;
}

export function ProblemTable({ problems, isLoading = false }: ProblemTableProps) {
  const { user } = useAuth();
  const [hoveredRow, setHoveredRow] = useState<string | null>(null);

  const getDifficultyBadgeClass = (difficulty: string) => {
    switch (difficulty.toLowerCase()) {
      case 'easy':
        return 'difficulty-easy';
      case 'medium':
        return 'difficulty-medium';
      case 'hard':
        return 'difficulty-hard';
      default:
        return '';
    }
  };

  const getStatusIcon = (problemId: string) => {
    // This would normally come from user progress data
    // For now, we'll show a mock status
    const statuses = ['solved', 'attempted', 'not-started'];
    const status = statuses[Math.floor(Math.random() * statuses.length)];

    switch (status) {
      case 'solved':
        return <i className="fas fa-check-circle text-success-500 text-lg" />;
      case 'attempted':
        return <i className="fas fa-minus-circle text-warning-500 text-lg" />;
      default:
        return <i className="fas fa-circle text-slate-300 dark:text-slate-600 text-lg" />;
    }
  };

  const canAccessProblem = (problem: Problem) => {
    return !problem.isPaid || (user?.subscriptionPlan === 'premium' && user?.subscriptionStatus === 'active');
  };

  if (isLoading) {
    return (
      <div className="bg-white dark:bg-slate-900 rounded-xl shadow-sm border border-slate-200 dark:border-slate-800 overflow-hidden">
        <div className="animate-pulse">
          {[...Array(5)].map((_, i) => (
            <div key={i} className="px-6 py-4 border-b border-slate-200 dark:border-slate-700">
              <div className="flex items-center space-x-4">
                <div className="w-6 h-6 bg-slate-200 dark:bg-slate-700 rounded-full"></div>
                <div className="flex-1">
                  <div className="h-4 bg-slate-200 dark:bg-slate-700 rounded w-1/3 mb-2"></div>
                  <div className="h-3 bg-slate-200 dark:bg-slate-700 rounded w-1/4"></div>
                </div>
                <div className="w-16 h-6 bg-slate-200 dark:bg-slate-700 rounded"></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  if (problems.length === 0) {
    return (
      <div className="bg-white dark:bg-slate-900 rounded-xl shadow-sm border border-slate-200 dark:border-slate-800 p-12 text-center">
        <i className="fas fa-search text-4xl text-slate-300 dark:text-slate-600 mb-4"></i>
        <h3 className="text-lg font-semibold text-slate-900 dark:text-white mb-2">
          No problems found
        </h3>
        <p className="text-slate-600 dark:text-slate-400">
          Try adjusting your filters or search terms.
        </p>
      </div>
    );
  }

  return (
    <div className="bg-white dark:bg-slate-900 rounded-xl shadow-sm border border-slate-200 dark:border-slate-800 overflow-hidden">
      <Table>
        <TableHeader>
          <TableRow className="bg-slate-50 dark:bg-slate-800 border-b border-slate-200 dark:border-slate-700">
            <TableHead className="w-16">Status</TableHead>
            <TableHead>Title</TableHead>
            <TableHead className="w-24">Acceptance</TableHead>
            <TableHead className="w-32">Difficulty</TableHead>
            <TableHead>Tags</TableHead>
            <TableHead className="w-20">Access</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {problems.map((problem, index) => (
            <TableRow
              key={problem.id}
              className={`hover:bg-slate-50 dark:hover:bg-slate-800 cursor-pointer transition-colors ${
                hoveredRow === problem.id ? 'bg-slate-50 dark:bg-slate-800' : ''
              }`}
              onMouseEnter={() => setHoveredRow(problem.id)}
              onMouseLeave={() => setHoveredRow(null)}
              data-testid={`row-problem-${index}`}
            >
              <TableCell>
                <Tooltip>
                  <TooltipTrigger>
                    {getStatusIcon(problem.id)}
                  </TooltipTrigger>
                  <TooltipContent>
                    <p>Problem status</p>
                  </TooltipContent>
                </Tooltip>
              </TableCell>

              <TableCell>
                {canAccessProblem(problem) ? (
                  <Link href={`/problem/${problem.id}`}>
                    <Button 
                      variant="link" 
                      className="p-0 h-auto font-medium text-slate-900 dark:text-white hover:text-brand-600 dark:hover:text-brand-400"
                      data-testid={`link-problem-${problem.title.replace(/\s+/g, '-').toLowerCase()}`}
                    >
                      {index + 1}. {problem.title}
                    </Button>
                  </Link>
                ) : (
                  <div className="flex items-center space-x-2">
                    <span className="font-medium text-slate-900 dark:text-white">
                      {index + 1}. {problem.title}
                    </span>
                    <i className="fas fa-lock text-amber-500 text-sm" />
                  </div>
                )}
              </TableCell>

              <TableCell>
                <span className="text-slate-600 dark:text-slate-400">
                  {problem.acceptanceRate}%
                </span>
              </TableCell>

              <TableCell>
                <Badge className={getDifficultyBadgeClass(problem.difficulty)}>
                  {problem.difficulty}
                </Badge>
              </TableCell>

              <TableCell>
                <div className="flex flex-wrap gap-1">
                  {(Array.isArray(problem.tags) ? problem.tags : []).slice(0, 3).map((tag: string) => (
                    <Badge
                      key={tag}
                      variant="outline"
                      className="text-xs bg-blue-50 text-blue-800 border-blue-200 dark:bg-blue-900/20 dark:text-blue-300 dark:border-blue-800"
                    >
                      {tag}
                    </Badge>
                  ))}
                  {Array.isArray(problem.tags) && problem.tags.length > 3 && (
                    <Badge variant="outline" className="text-xs">
                      +{problem.tags.length - 3}
                    </Badge>
                  )}
                </div>
              </TableCell>

              <TableCell>
                {problem.isPremium ? (
                  <Tooltip>
                    <TooltipTrigger>
                      <i className="fas fa-crown text-amber-500" />
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Premium only</p>
                    </TooltipContent>
                  </Tooltip>
                ) : (
                  <span className="text-slate-400 dark:text-slate-600 text-sm">Free</span>
                )}
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
}

client/src/components/providers/ThemePro.content:
import { createContext, useContext, useEffect, useState } from "react";

type Theme = "dark" | "light" | "system";

type ThemeProviderProps = {
  children: React.ReactNode;
  defaultTheme?: Theme;
  storageKey?: string;
};

type ThemeProviderState = {
  theme: Theme;
  setTheme: (theme: Theme) => void;
};

const initialState: ThemeProviderState = {
  theme: "system",
  setTheme: () => null,
};

const ThemeProviderContext = createContext<ThemeProviderState>(initialState);

export function ThemeProvider({
  children,
  defaultTheme = "system",
  storageKey = "vite-ui-theme",
  ...props
}: ThemeProviderProps) {
  const [theme, setTheme] = useState<Theme>(
    () => (localStorage.getItem(storageKey) as Theme) || defaultTheme
  );

  useEffect(() => {
    const root = window.document.documentElement;

    root.classList.remove("light", "dark");

    if (theme === "system") {
      const systemTheme = window.matchMedia("(prefers-color-scheme: dark)")
        .matches
        ? "dark"
        : "light";

      root.classList.add(systemTheme);
      return;
    }

    root.classList.add(theme);
  }, [theme]);

  const value = {
    theme,
    setTheme: (theme: Theme) => {
      localStorage.setItem(storageKey, theme);
      setTheme(theme);
    },
  };

  return (
    <ThemeProviderContext.Provider {...props} value={value}>
      {children}
    </ThemeProviderContext.Provider>
  );
}

export const useTheme = () => {
  const context = useContext(ThemeProviderContext);

  if (context === undefined)
    throw new Error("useTheme must be used within a ThemeProvider");

  return context;
};

