        try
        {
            var problems = await _searchService.SearchByTagsAsync(tags, count);
            return Ok(problems);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error searching by tags");
            return StatusCode(500, new { message = "An error occurred while searching by tags" });
        }
