// NEW RABBITMQ IMPLEMENTATION
using System.Text.Json;
using RabbitMQ.Client;
using RabbitMQ.Client.Events;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Options;
using DSAGrind.Common.Configuration;
using System.Text;

namespace DSAGrind.Common.Services;

public class RabbitMQService : IKafkaService, IDisposable
{
    private readonly RabbitMQSettings _rabbitMQSettings;
    private readonly ILogger<RabbitMQService> _logger;
    private IConnection? _connection;
    private IModel? _channel;
    private readonly object _connectionLock = new object();

    public RabbitMQService(IOptions<RabbitMQSettings> rabbitMQSettings, ILogger<RabbitMQService> logger)
    {
        _rabbitMQSettings = rabbitMQSettings.Value;
        _logger = logger;
        InitializeConnection();
    }

    private void InitializeConnection()
    {
        try
        {
            var factory = new ConnectionFactory()
            {
                Uri = new Uri(_rabbitMQSettings.ConnectionString),
                AutomaticRecoveryEnabled = true,
                NetworkRecoveryInterval = TimeSpan.FromSeconds(10)
            };

            _connection = factory.CreateConnection();
            _channel = _connection.CreateModel();

            _logger.LogInformation("RabbitMQ connection established successfully");
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to establish RabbitMQ connection");
            throw;
        }
    }

    private void EnsureConnection()
    {
        lock (_connectionLock)
        {
            if (_connection == null || !_connection.IsOpen || _channel == null || !_channel.IsOpen)
            {
                _logger.LogWarning("RabbitMQ connection lost, attempting to reconnect...");
                InitializeConnection();
            }
        }
    }

    public async Task PublishAsync<T>(string topic, T message, string? key = null, CancellationToken cancellationToken = default)
    {
        await PublishAsync(topic, message, null, key, cancellationToken);
    }

    public async Task PublishAsync<T>(string topic, T message, Dictionary<string, string>? headers, string? key = null, CancellationToken cancellationToken = default)
    {
        try
        {
            EnsureConnection();

            var messageJson = JsonSerializer.Serialize(message, new JsonSerializerOptions
            {
                PropertyNamingPolicy = JsonNamingPolicy.CamelCase
            });

            var body = Encoding.UTF8.GetBytes(messageJson);
            var exchangeName = $"{topic}-exchange";
            var queueName = topic;

            // Declare exchange and queue
            _channel?.ExchangeDeclare(exchange: exchangeName, type: ExchangeType.Direct, durable: true);
            _channel?.QueueDeclare(queue: queueName, durable: true, exclusive: false, autoDelete: false, arguments: null);
            _channel?.QueueBind(queue: queueName, exchange: exchangeName, routingKey: queueName);

            var properties = _channel?.CreateBasicProperties();
            if (properties != null)
            {
                properties.Persistent = true;
                properties.MessageId = key ?? Guid.NewGuid().ToString();
                properties.Timestamp = new AmqpTimestamp(DateTimeOffset.UtcNow.ToUnixTimeSeconds());

                if (headers != null)
                {
                    properties.Headers = new Dictionary<string, object>();
                    foreach (var header in headers)
                    {
                        properties.Headers[header.Key] = header.Value;
                    }
                }
            }

            _channel?.BasicPublish(exchange: exchangeName, routingKey: queueName, basicProperties: properties, body: body);

            _logger.LogDebug("Message published to RabbitMQ topic {Topic}", topic);

            await Task.CompletedTask; // For async compatibility
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to publish message to RabbitMQ topic {Topic}", topic);
            throw;
        }
    }

    public Task StartConsumerAsync<T>(string topic, string consumerGroup, Func<T, Dictionary<string, string>?, Task> messageHandler, CancellationToken cancellationToken = default)
    {
        try
        {
            EnsureConnection();

            var queueName = $"{topic}-{consumerGroup}";
            var exchangeName = $"{topic}-exchange";

            // Declare exchange and queue
            _channel?.ExchangeDeclare(exchange: exchangeName, type: ExchangeType.Direct, durable: true);
            _channel?.QueueDeclare(queue: queueName, durable: true, exclusive: false, autoDelete: false, arguments: null);
            _channel?.QueueBind(queue: queueName, exchange: exchangeName, routingKey: topic);

            if (_channel != null)
            {
                var consumer = new EventingBasicConsumer(_channel);
                consumer.Received += async (model, ea) =>
                {
                    try
                    {
                        var body = ea.Body.ToArray();
                        var messageJson = Encoding.UTF8.GetString(body);

                        var message = JsonSerializer.Deserialize<T>(messageJson, new JsonSerializerOptions
                        {
                            PropertyNamingPolicy = JsonNamingPolicy.CamelCase
                        });

                        if (message != null)
                        {
                            var headers = new Dictionary<string, string>();
                            if (ea.BasicProperties.Headers != null)
                            {
                                foreach (var header in ea.BasicProperties.Headers)
                                {
                                    headers[header.Key] = Encoding.UTF8.GetString((byte[])header.Value);
                                }
                            }

                            await messageHandler(message, headers);
                            _channel?.BasicAck(deliveryTag: ea.DeliveryTag, multiple: false);
                        }
                    }
                    catch (Exception ex)
                    {
                        _logger.LogError(ex, "Error processing message from RabbitMQ topic {Topic}", topic);
                        _channel?.BasicNack(deliveryTag: ea.DeliveryTag, multiple: false, requeue: true);
                    }
                };

                _channel.BasicConsume(queue: queueName, autoAck: false, consumer: consumer);
            }

            _logger.LogInformation("Started RabbitMQ consumer for topic {Topic} with consumer group {ConsumerGroup}", topic, consumerGroup);
            return Task.CompletedTask;
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to start RabbitMQ consumer for topic {Topic}", topic);
            throw;
        }
    }

    public Task StopConsumerAsync(CancellationToken cancellationToken = default)
    {
        _logger.LogInformation("Stopped RabbitMQ consumer");
        return Task.CompletedTask;
    }

    public void Dispose()
    {
        _channel?.Close();
        _channel?.Dispose();
        _connection?.Close();
        _connection?.Dispose();
    }
}

using System.Text.Json;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Options;
using DSAGrind.Common.Configuration;

namespace DSAGrind.Common.Services;

public class PerplexityAIService : IAIProviderService
{
    private readonly HttpClient _httpClient;
    private readonly ILogger<PerplexityAIService> _logger;
    private readonly AISettings _aiSettings;

    public PerplexityAIService(HttpClient httpClient, ILogger<PerplexityAIService> logger, IOptions<AISettings> aiSettings)
    {
        _httpClient = httpClient;
        _logger = logger;
        _aiSettings = aiSettings.Value;

        _httpClient.DefaultRequestHeaders.Add("Authorization", $"Bearer {_aiSettings.PerplexityApiKey}");
    }

    public async Task<string> GenerateHintAsync(string problemDescription, string userCode, int hintLevel, CancellationToken cancellationToken = default)
    {
        var prompt = $"""
            You are an expert programming tutor. Generate a helpful hint for this coding problem.

            Problem Description: {problemDescription}
            User's current code: {userCode}
            Hint Level: {hintLevel} (1=gentle nudge, 2=more specific, 3=detailed guidance)

            Provide a hint that guides the user without giving away the complete solution.
            Focus on the algorithmic approach or key insight needed.
            """;

        return await CallPerplexityAsync(prompt, cancellationToken);
    }

    public async Task<string> ExplainSolutionAsync(string problemDescription, string solutionCode, CancellationToken cancellationToken = default)
    {
        var prompt = $"""
            You are an expert programming educator. Explain this solution to a coding problem in detail.

            Problem Description: {problemDescription}
            Solution Code: {solutionCode}

            Provide a comprehensive explanation that covers:
            1. The algorithm/approach used
            2. Time and space complexity
            3. Key insights and why this approach works
            4. Line-by-line walkthrough of important parts

            Make it educational and easy to understand.
            """;

        return await CallPerplexityAsync(prompt, cancellationToken);
    }

    public async Task<string> AnalyzeCodeAsync(string code, string language, string? problemDescription = null, CancellationToken cancellationToken = default)
    {
        var prompt = $"""
            You are an expert code reviewer. Analyze this {language} code and provide detailed feedback.

            Code: {code}
            {(problemDescription != null ? $"Problem Description: {problemDescription}" : "")}

            Analyze and provide:
            1. Time complexity (Big O notation)
            2. Space complexity (Big O notation)
            3. Code quality score (1-100)
            4. List of suggestions for improvement
            5. List of issues or potential bugs
            6. Best practices recommendations
            7. Optimization tips

            Return your analysis in a structured format.
            """;

        return await CallPerplexityAsync(prompt, cancellationToken);
    }

    public async Task<string> GenerateTestCasesAsync(string problemDescription, int count, CancellationToken cancellationToken = default)
    {
        var prompt = $"""
            You are an expert test case designer. Generate {count} diverse test cases for this problem.

            Problem Description: {problemDescription}

            Generate test cases that cover:
            - Edge cases (empty input, single element, etc.)
            - Boundary conditions
            - Normal cases
            - Large inputs (if applicable)

            Format as:
            Input: [input]
            Expected Output: [output]
            ---
            (repeat for each test case)
            """;

        return await CallPerplexityAsync(prompt, cancellationToken);
    }

    public async Task<string> EstimateDifficultyAsync(string problemDescription, CancellationToken cancellationToken = default)
    {
        var prompt = $$$"""
            You are an expert programming problem curator. Analyze this problem and estimate its difficulty.

            Problem Description: {{{problemDescription}}}

            Provide your analysis in JSON format:
            {{
                "estimatedDifficulty": "Easy|Medium|Hard",
                "confidence": 0.85,
                "reasoningFactors": ["factor1", "factor2"],
                "estimatedTimeMinutes": 30,
                "requiredConcepts": ["concept1", "concept2"]
            }}

            Consider:
            - Algorithmic complexity required
            - Data structures needed
            - Problem-solving techniques
            - Implementation difficulty
            - Typical time to solve for different skill levels
            """;

        return await CallPerplexityAsync(prompt, cancellationToken);
    }

    public async Task<string> OptimizeCodeAsync(string code, string language, CancellationToken cancellationToken = default)
    {
        var prompt = $"""
            You are an expert code optimizer. Optimize this {language} code for better performance and readability.

            Original Code: {code}

            Provide an optimized version that:
            1. Improves time/space complexity if possible
            2. Follows best practices
            3. Is more readable and maintainable
            4. Includes brief comments explaining optimizations

            Return only the optimized code.
            """;

        return await CallPerplexityAsync(prompt, cancellationToken);
    }

    public async Task<string> DebugCodeAsync(string code, string language, string errorMessage, CancellationToken cancellationToken = default)
    {
        var prompt = $"""
            You are an expert debugger. Help fix this {language} code that's producing an error.

            Code: {code}
            Error Message: {errorMessage}

            Provide:
            1. Explanation of what's causing the error
            2. The corrected code
            3. Explanation of the fix
            4. Tips to avoid similar errors in the future

            Be clear and educational in your response.
            """;

        return await CallPerplexityAsync(prompt, cancellationToken);
    }

    public async Task<string> GenerateAlternativeSolutionAsync(string problemDescription, string existingCode, string language, CancellationToken cancellationToken = default)
    {
        var prompt = $"""
            You are an expert programmer. Generate an alternative solution to this problem using a different approach.

            Problem Description: {problemDescription}
            Existing Solution: {existingCode}
            Language: {language}

            Provide:
            1. A different algorithmic approach
            2. Complete alternative solution
            3. Comparison of time/space complexity
            4. Explanation of when to use each approach

            Make sure the alternative uses a genuinely different algorithm or technique.
            """;

        return await CallPerplexityAsync(prompt, cancellationToken);
    }

    private async Task<string> CallPerplexityAsync(string prompt, CancellationToken cancellationToken = default)
    {
        try
        {
            var requestData = new
            {
                model = _aiSettings.PerplexityModel,
                messages = new[]
                {
                    new { role = "system", content = "Be precise, concise, and educational in your responses." },
                    new { role = "user", content = prompt }
                },
                temperature = 0.2,
                top_p = 0.9,
                max_tokens = _aiSettings.MaxTokens,
                stream = false
            };

            var json = JsonSerializer.Serialize(requestData);
            var content = new StringContent(json, System.Text.Encoding.UTF8, "application/json");

            var response = await _httpClient.PostAsync("https://api.perplexity.ai/chat/completions", content, cancellationToken);

            if (!response.IsSuccessStatusCode)
            {
                var errorContent = await response.Content.ReadAsStringAsync(cancellationToken);
                _logger.LogError("Perplexity API error: {StatusCode} - {Content}", response.StatusCode, errorContent);
                return "I'm unable to process your request at the moment. Please try again later.";
            }

            var responseJson = await response.Content.ReadAsStringAsync(cancellationToken);
            var responseObj = JsonSerializer.Deserialize<JsonElement>(responseJson);

            return responseObj.GetProperty("choices")[0].GetProperty("message").GetProperty("content").GetString() 
                   ?? "Unable to generate response.";
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error calling Perplexity API");
            return "I'm experiencing technical difficulties. Please try again later.";
        }
    }
}

using System.Text.Json;
using Microsoft.Extensions.Logging;
using Microsoft.Extensions.Options;
using StackExchange.Redis;
using DSAGrind.Common.Configuration;

namespace DSAGrind.Common.Services;

public class RedisService : IRedisService, IDisposable
{
    private readonly RedisSettings _redisSettings;
    private readonly ILogger<RedisService> _logger;
    private readonly Lazy<ConnectionMultiplexer> _connectionMultiplexer;
    private readonly JsonSerializerOptions _jsonOptions;

    public RedisService(IOptions<RedisSettings> redisSettings, ILogger<RedisService> logger)
    {
        _redisSettings = redisSettings.Value;
        _logger = logger;

        _connectionMultiplexer = new Lazy<ConnectionMultiplexer>(() =>
        {
            var configuration = ConfigurationOptions.Parse(_redisSettings.ConnectionString);
            configuration.Password = _redisSettings.Password;
            configuration.ConnectTimeout = _redisSettings.ConnectTimeoutSeconds * 1000;
            configuration.SyncTimeout = _redisSettings.SyncTimeoutSeconds * 1000;
            configuration.CommandMap = CommandMap.Create(new HashSet<string>(), available: false);
            configuration.CommandMap = CommandMap.Default;
            configuration.AbortOnConnectFail = _redisSettings.AbortOnConnectFail;
            configuration.AllowAdmin = _redisSettings.AllowAdmin;
            configuration.ConnectRetry = _redisSettings.ConnectRetryCount;
            configuration.KeepAlive = _redisSettings.KeepAliveSeconds;

            return ConnectionMultiplexer.Connect(configuration);
        });

        _jsonOptions = new JsonSerializerOptions
        {
            PropertyNamingPolicy = JsonNamingPolicy.CamelCase
        };
    }

    private IDatabase Database => _connectionMultiplexer.Value.GetDatabase(_redisSettings.Database);
    private string GetKey(string key) => $"{_redisSettings.DefaultKeyPrefix}{key}";

    public async Task<T?> GetAsync<T>(string key, CancellationToken cancellationToken = default)
    {
        try
        {
            var value = await Database.StringGetAsync(GetKey(key));
            if (!value.HasValue)
                return default;

            if (typeof(T) == typeof(string))
                return (T)(object)value.ToString();

            return JsonSerializer.Deserialize<T>(value!, _jsonOptions);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error getting value from Redis for key {Key}", key);
            return default;
        }
    }

    public async Task<bool> SetAsync<T>(string key, T value, TimeSpan? expiration = null, CancellationToken cancellationToken = default)
    {
        try
        {
            string serializedValue;
            if (typeof(T) == typeof(string))
                serializedValue = value?.ToString() ?? string.Empty;
            else
                serializedValue = JsonSerializer.Serialize(value, _jsonOptions);

            return await Database.StringSetAsync(GetKey(key), serializedValue, expiration);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error setting value in Redis for key {Key}", key);
            return false;
        }
    }

    public async Task<bool> DeleteAsync(string key, CancellationToken cancellationToken = default)
    {
        try
        {
            return await Database.KeyDeleteAsync(GetKey(key));
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error deleting key from Redis {Key}", key);
            return false;
        }
    }

    public async Task<bool> ExistsAsync(string key, CancellationToken cancellationToken = default)
    {
        try
        {
            return await Database.KeyExistsAsync(GetKey(key));
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error checking if key exists in Redis {Key}", key);
            return false;
        }
    }

    public async Task<long> IncrementAsync(string key, long value = 1, CancellationToken cancellationToken = default)
    {
        try
        {
            return await Database.StringIncrementAsync(GetKey(key), value);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error incrementing value in Redis for key {Key}", key);
            return 0;
        }
    }

    public async Task<long> DecrementAsync(string key, long value = 1, CancellationToken cancellationToken = default)
    {
        try
        {
            return await Database.StringDecrementAsync(GetKey(key), value);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error decrementing value in Redis for key {Key}", key);
            return 0;
        }
    }

    public async Task<bool> ExpireAsync(string key, TimeSpan expiration, CancellationToken cancellationToken = default)
    {
        try
        {
            return await Database.KeyExpireAsync(GetKey(key), expiration);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error setting expiration for Redis key {Key}", key);
            return false;
        }
    }

    public async Task<TimeSpan?> GetTtlAsync(string key, CancellationToken cancellationToken = default)
    {
        try
        {
            return await Database.KeyTimeToLiveAsync(GetKey(key));
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error getting TTL for Redis key {Key}", key);
            return null;
        }
    }

    public async Task<bool> SetAddAsync<T>(string key, T value, CancellationToken cancellationToken = default)
    {
        try
        {
            var serializedValue = JsonSerializer.Serialize(value, _jsonOptions);
            return await Database.SetAddAsync(GetKey(key), serializedValue);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error adding to set in Redis for key {Key}", key);
            return false;
        }
    }

    public async Task<bool> SetRemoveAsync<T>(string key, T value, CancellationToken cancellationToken = default)
    {
        try
        {
            var serializedValue = JsonSerializer.Serialize(value, _jsonOptions);
            return await Database.SetRemoveAsync(GetKey(key), serializedValue);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error removing from set in Redis for key {Key}", key);
            return false;
        }
    }

    public async Task<bool> SetContainsAsync<T>(string key, T value, CancellationToken cancellationToken = default)
    {
        try
        {
            var serializedValue = JsonSerializer.Serialize(value, _jsonOptions);
            return await Database.SetContainsAsync(GetKey(key), serializedValue);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error checking set membership in Redis for key {Key}", key);
            return false;
        }
    }

    public async Task<T[]> SetMembersAsync<T>(string key, CancellationToken cancellationToken = default)
    {
        try
        {
            var values = await Database.SetMembersAsync(GetKey(key));
            return values.Select(v => JsonSerializer.Deserialize<T>(v!, _jsonOptions)!).ToArray();
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error getting set members from Redis for key {Key}", key);
            return Array.Empty<T>();
        }
    }

    public async Task<bool> HashSetAsync<T>(string key, string field, T value, CancellationToken cancellationToken = default)
    {
        try
        {
            var serializedValue = JsonSerializer.Serialize(value, _jsonOptions);
            return await Database.HashSetAsync(GetKey(key), field, serializedValue);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error setting hash field in Redis for key {Key} field {Field}", key, field);
            return false;
        }
    }

    public async Task<T?> HashGetAsync<T>(string key, string field, CancellationToken cancellationToken = default)
    {
        try
        {
            var value = await Database.HashGetAsync(GetKey(key), field);
            if (!value.HasValue)
                return default;

            return JsonSerializer.Deserialize<T>(value!, _jsonOptions);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error getting hash field from Redis for key {Key} field {Field}", key, field);
            return default;
        }
    }

    public async Task<bool> HashDeleteAsync(string key, string field, CancellationToken cancellationToken = default)
    {
        try
        {
            return await Database.HashDeleteAsync(GetKey(key), field);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error deleting hash field from Redis for key {Key} field {Field}", key, field);
            return false;
        }
    }

    public async Task<bool> HashExistsAsync(string key, string field, CancellationToken cancellationToken = default)
    {
        try
        {
            return await Database.HashExistsAsync(GetKey(key), field);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error checking hash field existence in Redis for key {Key} field {Field}", key, field);
            return false;
        }
    }

    public async Task<Dictionary<string, T>> HashGetAllAsync<T>(string key, CancellationToken cancellationToken = default)
    {
        try
        {
            var hash = await Database.HashGetAllAsync(GetKey(key));
            var result = new Dictionary<string, T>();

            foreach (var item in hash)
            {
                result[item.Name!] = JsonSerializer.Deserialize<T>(item.Value!, _jsonOptions)!;
            }

            return result;
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error getting all hash fields from Redis for key {Key}", key);
            return new Dictionary<string, T>();
        }
    }

    public async Task<long> ListPushAsync<T>(string key, T value, bool toLeft = true, CancellationToken cancellationToken = default)
    {
        try
        {
            var serializedValue = JsonSerializer.Serialize(value, _jsonOptions);
            return toLeft 
                ? await Database.ListLeftPushAsync(GetKey(key), serializedValue)
                : await Database.ListRightPushAsync(GetKey(key), serializedValue);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error pushing to list in Redis for key {Key}", key);
            return 0;
        }
    }

    public async Task<T?> ListPopAsync<T>(string key, bool fromLeft = true, CancellationToken cancellationToken = default)
    {
        try
        {
            var value = fromLeft 
                ? await Database.ListLeftPopAsync(GetKey(key))
                : await Database.ListRightPopAsync(GetKey(key));

            if (!value.HasValue)
                return default;

            return JsonSerializer.Deserialize<T>(value!, _jsonOptions);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error popping from list in Redis for key {Key}", key);
            return default;
        }
    }

    public async Task<long> ListLengthAsync(string key, CancellationToken cancellationToken = default)
    {
        try
        {
            return await Database.ListLengthAsync(GetKey(key));
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error getting list length from Redis for key {Key}", key);
            return 0;
        }
    }

    public async Task<T[]> ListRangeAsync<T>(string key, long start = 0, long stop = -1, CancellationToken cancellationToken = default)
    {
        try
        {
            var values = await Database.ListRangeAsync(GetKey(key), start, stop);
            return values.Select(v => JsonSerializer.Deserialize<T>(v!, _jsonOptions)!).ToArray();
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error getting list range from Redis for key {Key}", key);
            return Array.Empty<T>();
        }
    }

    public async Task<bool> IsRateLimitedAsync(string key, int limit, TimeSpan window, CancellationToken cancellationToken = default)
    {
        var result = await CheckRateLimitAsync(key, limit, window, cancellationToken);
        return !result.IsAllowed;
    }

    public async Task<RateLimitResult> CheckRateLimitAsync(string key, int limit, TimeSpan window, CancellationToken cancellationToken = default)
    {
        try
        {
            var rateLimitKey = GetKey($"rate_limit:{key}");
            var count = await Database.StringIncrementAsync(rateLimitKey);

            if (count == 1)
            {
                await Database.KeyExpireAsync(rateLimitKey, window);
            }

            var ttl = await Database.KeyTimeToLiveAsync(rateLimitKey) ?? TimeSpan.Zero;

            return new RateLimitResult
            {
                IsAllowed = count <= limit,
                Count = count,
                Limit = limit,
                ResetTime = ttl
            };
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error checking rate limit for key {Key}", key);
            return new RateLimitResult { IsAllowed = true, Count = 0, Limit = limit };
        }
    }

    public void Dispose()
    {
        if (_connectionMultiplexer.IsValueCreated)
        {
            _connectionMultiplexer.Value.Dispose();
        }
    }
}

