public class RefreshToken
{
    [BsonElement("token")]
    public string Token { get; set; } = string.Empty;

    [BsonElement("expires")]
    public DateTime Expires { get; set; }

    [BsonElement("created")]
    public DateTime Created { get; set; } = DateTime.UtcNow;

